export type Size = "S" | "M" | "L" | "XL" | "XXL";

export type Product = {
  slug: string;
  title: string;
  price: number;
  sizes: Size[];
  colors: { name: string; hex: string }[];
  images: string[];
  description: string;
  category: "inferiores" | "superiores" | "ropa-interior" | "summer";
};

// Helper que soporta tanto IDs de Pexels como URLs absolutas
const P = (pathOrUrl: string, w = 1600) => {
  if (/^https?:\/\//.test(pathOrUrl)) return pathOrUrl; // passthrough si es URL completa
  return `https://images.pexels.com/photos/${pathOrUrl}?auto=compress&cs=tinysrgb&w=${w}&fit=crop`;
};

